import React from 'react';
import { useSpring, animated } from '@react-spring/web';
import { 
  TrendingUp, 
  AlertCircle, 
  CheckCircle, 
  Users, 
  Target,
  Shield,
  Lightbulb,
  Zap
} from 'lucide-react';

const InsightCard = ({ insight }) => {
  const cardAnimation = useSpring({
    from: { opacity: 0, transform: 'translateY(20px) scale(0.95)' },
    to: { opacity: 1, transform: 'translateY(0px) scale(1)' },
    config: { tension: 300, friction: 25 }
  });

  const getImpactColor = (impact) => {
    switch(impact) {
      case 'Critical': return 'from-red-500 to-red-600';
      case 'High': return 'from-yellow-500 to-orange-500';
      case 'Medium': return 'from-blue-500 to-blue-600';
      default: return 'from-gray-500 to-gray-600';
    }
  };

  const getImpactIcon = (impact) => {
    switch(impact) {
      case 'Critical': return AlertCircle;
      case 'High': return TrendingUp;
      case 'Medium': return CheckCircle;
      default: return Lightbulb;
    }
  };

  const getCategoryIcon = (category) => {
    switch(category) {
      case 'Partnership Strategy': return Users;
      case 'Technical Architecture': return Target;
      case 'Technology Innovation': return Zap;
      case 'Competitive Strategy': return Shield;
      case 'Product Enhancement': return TrendingUp;
      case 'Market Expansion': return CheckCircle;
      default: return Lightbulb;
    }
  };

  const getImpactBadgeStyle = (impact) => {
    switch(impact) {
      case 'Critical': return 'bg-red-100 text-red-800 border-red-200';
      case 'High': return 'bg-yellow-100 text-yellow-800 border-yellow-200';
      case 'Medium': return 'bg-blue-100 text-blue-800 border-blue-200';
      default: return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };

  const getCategoryBadgeStyle = (category) => {
    const categoryColors = {
      'Partnership Strategy': 'bg-purple-100 text-purple-800 border-purple-200',
      'Technical Architecture': 'bg-green-100 text-green-800 border-green-200',
      'Technology Innovation': 'bg-indigo-100 text-indigo-800 border-indigo-200',
      'Competitive Strategy': 'bg-orange-100 text-orange-800 border-orange-200',
      'Product Enhancement': 'bg-teal-100 text-teal-800 border-teal-200',
      'Market Expansion': 'bg-pink-100 text-pink-800 border-pink-200'
    };
    return categoryColors[category] || 'bg-gray-100 text-gray-800 border-gray-200';
  };

  const ImpactIcon = getImpactIcon(insight.impact);
  const CategoryIcon = getCategoryIcon(insight.category);

  return (
    <animated.div
      style={cardAnimation}
      className="bg-white rounded-2xl p-6 shadow-lg border border-gray-100 hover:shadow-xl transition-all duration-300 hover:-translate-y-1 relative overflow-hidden group"
    >
      {/* Background accent */}
      <div className={`absolute inset-0 bg-gradient-to-br ${getImpactColor(insight.impact)} opacity-5`} />

      {/* Content */}
      <div className="relative">
        {/* Header */}
        <div className="flex items-start gap-4 mb-4">
          <div className={`w-12 h-12 rounded-xl bg-gradient-to-br ${getImpactColor(insight.impact)} flex items-center justify-center shadow-lg`}>
            <ImpactIcon className="w-6 h-6 text-white" />
          </div>

          <div className="flex-1">
            <h3 className="text-lg font-bold text-gray-900 mb-2 group-hover:text-blue-600 transition-colors">
              {insight.title}
            </h3>

            <div className="flex flex-wrap gap-2">
              <span className={`px-3 py-1 rounded-full text-xs font-medium border ${getImpactBadgeStyle(insight.impact)}`}>
                {insight.impact} Impact
              </span>
              <span className={`px-3 py-1 rounded-full text-xs font-medium border ${getCategoryBadgeStyle(insight.category)}`}>
                <CategoryIcon className="w-3 h-3 inline mr-1" />
                {insight.category}
              </span>
            </div>
          </div>
        </div>

        {/* Description */}
        <p className="text-gray-700 text-sm leading-relaxed mb-4">
          {insight.description}
        </p>

        {/* Impact indicator bar */}
        <div className={`h-1 w-full bg-gradient-to-r ${getImpactColor(insight.impact)} rounded-full opacity-20 group-hover:opacity-40 transition-opacity`} />
      </div>

      {/* Hover effect overlay */}
      <div className="absolute inset-0 bg-gradient-to-br from-transparent via-transparent to-blue-50 opacity-0 group-hover:opacity-100 transition-opacity duration-300 pointer-events-none" />
    </animated.div>
  );
};

export default InsightCard;
